import { createStore } from 'vuex'
import createPersistedState from "vuex-persistedstate"

const store = createStore({
  state: {
    toggleFooter: true,
    toggleHeader: true,
    toggleSidebar: true,
    destination: '/',
    nombreCompleto: '',
    userData: {
        idUser: 0,
        nick: 'cyz',
        email: '',
        role: '',
        lastSuccesfullAccess: '',
        jwt: '', //  jwt='' => signed: false,
        usuarioDetalle: null,
    },
    errorMsg: ''    
  },
  getters: {
    getJwt2 (state) {
      return state.userData.jwt2
    },    
    getjwt2(state){
      return state.userData.jwt2
    }
  },
  mutations: {
    setJwt2(state, jwt2) {
      state.userData.jwt2 = jwt2;
    },
    setUserData(state, data) {        
        state.userData = data;
        state.userData.role =  data.role != undefined ? data.role.toLowerCase():'';        
        /*state.userData.idUser = data.idUser;
        state.userData.nick = data.nick;
        state.userData.email = data.email;
        state.userData.role = data.role;
        state.userData.lastSuccesfullAccess = data.lastSuccesfullAccess;
        state.userData.jwt = data.jwt;
        state.userData.usuarioDetalle = data.usuarioDetalle;*/
    },
    setDestination(state, destination) {
        state.destination = destination;
    },
    setNombreCompleto(state, nombreCompleto) {
        state.nombreCompleto = nombreCompleto;
    },
    deleteImage(state, id) {
        const imgIndex = state.userData.usuarioDetalle.findIndex(img => img.id === id);
        if (imgIndex !== -1) {
            state.userData.usuarioDetalle.splice(imgIndex, 1);
        }
    },
    deleteAllImages(state) {
        state.userData.usuarioDetalle = [];
    },
    deleteUserData(state) {
        state.userData = {
            idUser: 0,
            nick: 'cyz',
            email: '',
            role: '',
            lastSuccesfullAccess: '',
            jwt: '',
            usuarioDetalle: null,
            jwt2: 'goose'
        };
    }
  },
  plugins: [createPersistedState()]
});

export default store;
